  //       resolve(res.data)
  //     }
  //   )
  // })
  // return await promise

  const res = await fetch('http://127.0.0.1:7001/default/getArticleList')
  const json = await res.json()
  return { stars: json.stargazers_count }

  return axios('http://127.0.0.1:7001/default/getArticleList').then((res)=>{
    console.log('远程获取数据结果:',res.data)
    return res.data
  }).catch((err) => {
    console.log(err)
    return err
  })
}


export default Home
