import Header from '../../components/Header'
import Rainbow from '../../components/Rainbow'
import Api  from '../../config/apiUrl'
import axios from 'axios'
import './style.less'


const Categories = ({ categoryList, articleList }) => {
  return (
    <div>
      <Rainbow />
      <div className="container">
        <Header />
        <div className="categories">
          <span>类别：</span>
          <div className="list">
            {
              categoryList.map(item => {
                const { type_name, type_id } = item
                return (
                  <a href="">{type_name}</a>
                )
              })
            }
          </div>
        </div>
        <div class="category-list">
          {
            articleList.map(item => {
              const { article_title } = item
              return (
                <div>{article_title}</div>
              )
            })
          }
        </div>
      </div>
    </div>

  )
}

Categories.getInitialProps = async() => {
  const categoryList = await axios(Api.getCategories).then((res)=>{
    return res.data
  }).catch((err) => {
    console.log(err)
    return { err }
  })

  // 默认选第一个分类
  const typeId = categoryList[0].type_id
  const articleList = await axios(Api.getArticleByCategory, {
    params: {
      type: typeId
    }
  }).then((res)=>{
    return res.data
  }).catch((err) => {
    console.log(err)
    return { err }
  })
  return {
    categoryList,
    articleList
  }
}

export default Categories
